# compute effect size from two group pre-post differenceses.neteffect <- function(m01,m02,m11,m12,sd0,sd1,n0,n1) {  message(paste0("m01 <- ",m01," # control group mean at baseline"))  message(paste0("m02 <- ",m02," # control group mean at follow-up"))  message(paste0("m11 <- ",m11," # treatment group mean at baseline"))  message(paste0("m12 <- ",m12," # treatment group mean at follow-up"))  message(paste0("n0 <- ",n0," # control group sample size"))  message(paste0("n1 <- ",n1," # treatment group sample size"))  message(paste0("sd0 <- ",sd0," # control group standard deviation at baseline"))  message(paste0("sd1 <- ",sd1," # treatment group standard deviation at baseline"))  message(paste0("pooled.sd <- sqrt((((n0-1)*sd0^2)+((n1-1)*sd1^2))/(n0+n1-2))"))  message(paste0("net.effect <- (m12-m11)-(m02-m01)"))  net.effect <- (m12-m11)-(m02-m01)  net.effect.show <- round(net.effect,digits=3)  message(paste0("# net.effect is ",net.effect.show))  pooled.sd <- sqrt((((n0-1)*sd0^2)+((n1-1)*sd1^2))/(n0+n1-2))  pooled.sd <- as.double(pooled.sd)  pooled.sd.show <- round(pooled.sd,digits=3)  message(paste0("# pooled.sd is ",pooled.sd.show))  es.neteffect <- net.effect/pooled.sd  es.neteffect.show <- round(es.neteffect,digits=3)  message(paste0("effect.size <- net.effect / pooled.sd "))  message(paste0("effect.size"))  es.neteffect}#es.neteffect(56.6,46.0,55.1,51.0,14.2,9.1,19,11) #CDI#es.neteffect(4.2,2.0,4.3,1.2,2.5,2.8,19,11) #DBDRSes.neteffect(58.288,51.009,59.991,46.027,14.2,9.1,19,11)