# compute effect size (d) from mean, sd, nesiD <- function(m1,sd1,n1,m2,sd2,n2) {  # Uses weighted pooled SD, so produces Hedge's g version of Cohen's d  # 95% Confidence interval from Hedge LV Oklin I. Statistical methods for   # meta-analysis. Orlando, Academic Press Inc., 2014 p86 per Dong Kyu Lee,  # Alternatives to P value: confidence interval and effect size. Korean   # Journal of Anaesthesiology 2016,69(6):555-562  pooled.sd <- sqrt((((n1-1)*sd1^2)+((n2-1)*sd2^2))/(n1+n2-2))  d <-(m1-m2)/pooled.sd  vard <- sqrt(((n1+n2)/(n1*n2))+(d^2/(2*(n1+n2))))  alpha <- .05  d.ll <- d+qnorm(alpha/2)*vard  d.ul <- d+qnorm(1-(alpha/2))*vard  message(paste0("Cohen's d (Hedge's g), the standardized mean difference: ", sprintf("%0.2f", d)))  message(paste0("d = ","(",m1," - ",m2,")/(sqrt((((",n1,"-1)*",sd1,"^2)+((",n2,"-1)*",sd2,"^2))/(",n1," + ",n2,"-2)))"))  message(paste0("95% CI on d: ", sprintf("%0.2f", d.ll) ,"," , sprintf("%0.2f", d.ul) ))  dobj <- c(d,d.ll,d.ul)  dobj}#dis <- esiD(0.7,0.5,317,1.1,0.8,24)#dis[1]
