# compute effect size (d) from mean, se, nesiDse <- function(m1,se1,n1,m2,se2,n2) {  sd1 <- se1*sqrt(n1)  sd2 <- se2*sqrt(n2)  pooled.sd <- sqrt((((n1-1)*sd1^2)+((n2-1)*sd2^2))/(n1+n2-2))  d <-(m1-m2)/pooled.sd  message(paste0("Cohen's d, the standardized mean difference: ", sprintf("%0.2f", d)))  sd1.is <- paste0("(",se1,"*sqrt(",n1,"))")  sd2.is <- paste0("(",se2,"*sqrt(",n2,"))")  message(paste0("d = ","(",m1," - ",m2,")/(sqrt((((",n1,"-1)*",sd1.is,"^2)+((",n2,"-1)*",sd2.is,"^2))/(",n1," + ",n2,"-2)))"))  d}

