# Define the hist_discrete function with precise break controlhist_discrete <- function(data, variable = NULL) {   # Check if variable is NULL, which implies direct vector input   if (is.null(variable)) {      # data is assumed to be the vector of values      values <- data   } else {      # Extract the variable from the dataframe      values <- pull(data, {{ variable }})   }      # Remove missing values   values <- na.omit(values)      # Check if there are any values left to plot   if (length(values) == 0) {      message("No data available to plot after removing missing values.")      return()   }      # Calculate the exact breaks needed   min_val <- floor(min(values))   max_val <- ceiling(max(values))   # Create breaks such that there is one for each integer from the smallest to the largest   breaks <- seq(min_val - 0.5, max_val + 0.5, by = 1)      # Create a histogram with these breaks   hist(values, breaks = breaks, right = FALSE,        main = paste("Histogram of", deparse(substitute(variable))),        xaxt = 'n')  # Turn off default x-axis to customize      # Custom x-axis   axis(1, at = min_val:max_val, labels = min_val:max_val)}# Example usage with the Palmer Penguins data# library(palmerpenguins)# data(penguins)# Visualizing the 'year' column# hist_discrete(penguins, "year")