# pooled sd pooled.sd <- function(sd1,sd2,n1,n2) {   pooled.sd <- sqrt((((n1-1)*sd1^2)+((n2-1)*sd2^2))/(n1+n2-2))   pooled.sd <- as.double(pooled.sd)   show.pooled.sd <- round(pooled.sd,digits=3)   message(paste0("n1 <- ",n1))   message(paste0("n2 <- ",n2))   message(paste0("sd1 <- ",sd1))   message(paste0("sd2 <- ",sd2))   message(paste0("pooled.sd <- sqrt((((n1-1)*sd1^2)+((n2-1)*sd2^2))/(n1+n2-2))"))   message(paste0("# pooled.sd = sqrt((((",n1,"-1)*",sd1,"^2)+((",n2,"-1)*",sd2,"^2))/(",n1,"+",n2,"-","2))"))   a <- round((n1-1)*sd1^2,digits = 3)   b <- round((n2-1)*sd2^2,digits = 3)   c <- round((n1+n2-2),digits= 3)   message(paste0("# pooled.sd = sqrt((((",a,")+((",b,"))/(",c,"))"))   message(paste0("# pooled.sd = ",show.pooled.sd))   pooled.sd}# pooled.sd(sd1=0.43,sd2=0.442,n1=48,n2=144)
