
showmeSection <- function(x, search_string = NULL) {   # Read the contents of the file   file_contents <- readLines(x)      # Start displaying from the first line if no search string is provided   start_index <- 1      # If a search string is provided, find the line where it first appears   if (!is.null(search_string)) {      matches <- grep(search_string, file_contents, fixed = TRUE)      if (length(matches) > 0) {         start_index <- matches[1]      }   }      # Initialize a counter for consecutive blank lines   blank_line_count <- 0      # Print the contents starting from the matched line or the beginning   for (i in start_index:length(file_contents)) {      if (file_contents[i] == "") {         blank_line_count <- blank_line_count + 1      } else {         blank_line_count <- 0      }            # Stop printing after encountering three consecutive blank lines      if (blank_line_count == 3) {         break      }            cat(file_contents[i], "\n")   }}